import React, { Component, Fragment } from 'react';
import TeamList from './components/TeamList'
import CharacterList from './components/CharacterList'
import Nav from './components/Nav'
import {getGryffindor} from './endpoints'
import './App.css';

class App extends Component {
  state = {
    students: [],
    team: [],
    userInput: ""
  }

  componentDidMount(){
    fetch(getGryffindor)
    .then(data => data.json())
    .then(data => this.setState({ students: data[0].members }))
  }

  handleChange = (event) => {
    this.setState({userInput: event.target.value})
  }

  handleAdd = (id) => {
    const studentArray = this.state.students.filter(student => student._id !== id)
    const newMember = this.state.students.find(student => student._id === id)
    const teamArray = [...this.state.team, newMember]
    this.setState({students: studentArray, team: teamArray})
  }

  handleRemove = (id) => {
    const teamArray = this.state.team.filter(student => student._id !== id)
    const newMember = this.state.team.find(student => student._id === id)
    const studentArray = [newMember, ...this.state.students]
    this.setState({students: studentArray, team: teamArray})
  }

  applyFilter = (array) => {
    if(this.state.userInput !== ""){
      const newArray = array.filter(student => student.name.toLowerCase().includes(this.state.userInput.toLowerCase()))
      return newArray
    } else {
      return array
    }
  }

  changeStudentInfo = (info) => {
      const newStudents = this.state.students.map(student => {
        if(student._id === info._id){
          return info
        } else {
          return student
        }
      })
      const newTeam = this.state.team.map(student => {
        if(student._id === info._id){
          return info
        } else {
          return student
        }
      })

      this.setState({
        team: newTeam,
        students: newStudents
      })
  }

  render() {
    return (
      <Fragment>
        <Nav handleChange={this.handleChange} userInput={this.state.userInput}/>
        <div id="character-container">
          <CharacterList changeStudentInfo={this.changeStudentInfo} handleClick={this.handleAdd} students={this.applyFilter(this.state.students)}/>
          <TeamList changeStudentInfo={this.changeStudentInfo} handleClick={this.handleRemove} students={this.applyFilter(this.state.team)}/>
        </div>
      </Fragment>
    );
  }
}

export default App;
