import React, {Component} from 'react';
import './App.css';
import {getGryffindor} from './endpoints'
import StudentContainer from './components/CharacterList'
import Filter from './components/Nav'
import TeamContainer from './components/TeamList'

class App extends Component {

  state = {
    allStudents: [],
    teamStudents: []
  }

  componentDidMount(){
    fetch(getGryffindor)
    .then(res => res.json())
    .then(students => this.setState({ allStudents: students[0].members }))
  }

  handleToggle = () => {
    console.log("this worked");
  }

  render(){

    return (
      <div id="character-container">
        <Filter/>
        <StudentContainer handleToggle={this.handleToggle} students={this.state.allStudents}/>
        <TeamContainer handleToggle={this.handleToggle} teamStudents={this.state.teamStudents}/>

      </div>
    )
  }
}

export default App;
