import React, {Component} from 'react';
import './App.css';
import {getGryffindor} from './endpoints'
import StudentContainer from './components/CharacterList'
import Filter from './components/Nav'
import TeamContainer from './components/TeamList'

class App extends Component {

  state = {
    allStudents: [],
    teamStudents: []
  }

  componentDidMount(){
    fetch(getGryffindor)
    .then(res => res.json())
    .then(students => this.setState({ allStudents: students[0].members }))
  }

  handleAdd = (studentData) => {
    // console.log(this.state.allStudents);
    const newStudentArray = [...this.state.allStudents].filter(student => student._id !== studentData._id)
    // const foundStudent = this.state.allStudents.find(student => student._id === studentData._id)
    // console.log(foundStudent);
    const newTeamStudents = [studentData, ...this.state.teamStudents]

    this.setState({
      allStudents: newStudentArray,
      teamStudents: newTeamStudents
    })
  }

  handleRemove = (studentData) => {
    const newStudentArray = [studentData, ...this.state.allStudents]

    const newTeamStudents = [...this.state.teamStudents].filter(student => student._id !== studentData._id)

    this.setState({
      allStudents: newStudentArray,
      teamStudents: newTeamStudents
    })
  }

  render(){

    return (
      <div>
        <Filter/>
        <div id="character-container">
        <StudentContainer handleToggle={this.handleAdd} students={this.state.allStudents}/>
        <TeamContainer handleToggle={this.handleRemove} teamStudents={this.state.teamStudents}/>
        </div>
      </div>
    )
  }
}

export default App;
